// src/store/index.js
import Vue from 'vue';
import Vuex from 'vuex';
import mutations from './mutations'; // Import mutations
import actions from './actions'; // Import actions
import getters from './getters'; // Import getters

Vue.use(Vuex);

export default new Vuex.Store({
  state: {
    products: [], // Khởi tạo state chứa sản phẩm
  },
  mutations, // Thêm mutations vào store
  actions, // Thêm actions vào store
  getters, // Thêm getters vào store
});
