// src/store/actions.js
import APIURL from '../constraint';
export default {
  async fetchProducts({ commit, state }) {
    // Kiểm tra nếu dữ liệu đã được fetch rồi, không gọi lại API nữa
    if (state.products.length > 0) {
      return; // Dữ liệu đã có, không làm gì cả
    }

    try {
      const response = await fetch(`${APIURL}productDrink`);
      const data = await response.json();
      commit('setProducts', data); // Gọi mutation để cập nhật state
    } catch (error) {
      console.error('Error fetching products:', error);
    }
  },
};
