// thay đổi trực tiếp state
const mutations={
    // pt ko có tham số
    increment(state)
    {
        state.count++
    },
    setName(state,newname)
    {
        state.name=newname
    },
    SET_PRODUCTS(state,products)
    {
        state.products=products
    }
}
export default mutations
