import axios from 'axios';
import APIURL from '@/constraint';

const actions = {
  async fetchProducts({ commit }) {
    try {
      const response = await axios.get(`${APIURL}/productDrink`); // Gọi API
      commit('SET_PRODUCTS', response.data); // Gửi dữ liệu tới mutation
    } catch (error) {
      console.error('Lỗi khi gọi API productDrink:', error); // Log lỗi để debug
    }
  },
};

export default actions;
