  },
  {
    path: '/tea',
    name: 'Tea',
    component: Tea,
  },
  {
    path: '/gioithieu',
    name: 'GioiThieu',
    component: GioiThieu,
  },
  {
    path: '/lienhe',
    name: 'LienHe',
    component: LienHe,
  },
  {
    path: '/about',
    name: 'About',
    component: About,
  },
  {
    path: '/tintuc',
    name: 'TinTuc',
    component: TinTuc,
  },
  {
    path: '/product/:id',
    name: 'ProductDetail',
    component: ProductDetail,
  },
  {
    path: '/cart',
    name: 'Cart',
    component: Cart,
  },
];

const router = createRouter({
  history: createWebHistory(),
  routes,
});

export default router;

